/* Tmd Vendor Communication */
$communication = array();
if ($this->user->hasPermission('access', 'extension/module/tmdcommunication_setting')) {
	$communication[] = array(
		'name'	   => $this->language->get('text_c_setting'),
		'href'     => $this->url->link('extension/module/tmdcommunication_setting', 'token=' . $this->session->data['token'], true),
		'children' => array()
	);
}
if ($this->user->hasPermission('access', 'vendor/contact')) {
	$communication[] = array(
		'name'	   => $this->language->get('text_vendorcontact'),
		'href'     => $this->url->link('vendor/contact', 'token=' . $this->session->data['token'], true),
		'children' => array()
	);
}

if ($communication) {
	$data['menus'][] = array(
		'id'       => 'menu-communication',
		'icon'	   => 'fa-th-list fw',
		'name'	   => $this->language->get('text_communication'),
		'href'     => '',
		'children' => $communication
	);
}
/* Tmd Vendor Communication */

